var mapOpen=document.querySelector(".big-map"),mapPopup=document.querySelector(".pop-big-map"),mapClose=document.querySelector(".mapClose");mapOpen.addEventListener("click",function(a){a.preventDefault(),mapPopup.classList.add("modal-content-show"),mapPopup.classList.add("modal-animation")}),mapClose.addEventListener("click",function(a){a.preventDefault(),mapPopup.classList.remove("modal-content-show"),mapPopup.classList.remove("modal-animation")});